import React, { useState } from "react";
import "./booking.css";
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from "reactstrap";
import { useNavigate } from "react-router-dom";


const Booking = ({ tour, avgRating }) => {
  const { price, reviews} = tour;
  const navigate = useNavigate();

  const [credentials, setCredentials] = useState({
    usedId: "01",
    userEmail: "example@gmail.com",
    fullName: "",
    phone: "",
    guestSize: 1,
    bookAt: ""
  });

  const handleChange = e => {setCredentials(prev => ({...prev, [e.target.id]: e.target.value}))};

  const serviceFree = 10;
  const totalAmount = Number(price) * Number(credentials.guestSize) + Number(serviceFree)
  
  //send data to the server 
  const handleClick = e => {
    e.preventDefault();

    // console.log(credentials);
    navigate("/thank-you");
  }
  return (
    <div className="booking">
      <div className="booking__top d-flex align-items-center justify-content-between">
        <h3>
          ${price}
          <span> /per person</span>
        </h3>
        <span className="tour__rating d-flex align-items-center">
          <i
            className="ri-star-s-fill"
            // style={{ color: "var(--secondary-color)"}}
          ></i>
          {avgRating === 0 ? null : avgRating} ({reviews?.length})
        </span>
      </div>
      {/* ========= booking form ========= */}
      <div className="booking__form">
        <h3>Information</h3>
        <Form className="booking__info-form" onSubmit={handleClick}>
            <FormGroup>
              <input type="text" placeholder="Full Name" id = "fullName" 
              required onChange = {handleChange} />
            </FormGroup>
            <FormGroup>
              <input type="number" placeholder="phone" id = "phone" 
              required onChange = {handleChange} />
            </FormGroup>
            <FormGroup className="d-flex align-items-center gap-3">
              <input type="date" placeholder="" id = "bookAt" 
              required onChange = {handleChange} />
              <input type="number" placeholder="Guest" id = "guestSize" 
              required onChange = {handleChange} />
            </FormGroup>
        </Form>
      </div>
      {/* =========== booking end ============== */}

      {/* ================ booking bottom start ============ */}
      <div className="booking__bottom">
        <ListGroup>
        <ListGroupItem className="border-0 px-0">
          <h5  className="d-flex align-items-center gap-1">
            ${price} <i class = "ri-close-line" ></i> {credentials.guestSize} person</h5>
          <span>${price * credentials.guestSize}</span>
        </ListGroupItem>
        <ListGroupItem className="border-0 px-0">
          <h5>Service charges</h5>
          <span>$10</span>
        </ListGroupItem>
        <ListGroupItem className="border-0 px-0 total">
          <h5>Total</h5>
          <span>${totalAmount}</span>
        </ListGroupItem>
        </ListGroup>
        
        <Button className="btn primary__btn w-100 mt-4" onClick={handleClick}>Book now</Button>
      </div>
      {/* ================ booking bottom end============ */}
    </div>
  );
};

export default Booking;
