import React from 'react';
import {Card, CardBody} from "reactstrap";
import {Link} from "react-router-dom";


const TourCard = ({tour}) => {

  const {id, title, photo, city, price, featured, avgRating, reviews} = tour;

  return (
    <div className='tour__card'>
      <Card>
        <div className="tour__img">
          <img src={photo} alt="tour-img" />
          <span>Featured</span>
        </div>
      </Card>

      <CardBody>
         <div className="card__top d-flex align-items-center
         justify-content-between">
            <span className='tour__location d-flex align-items-center gap-1'>
               <i className="ri-map-pin-line"></i> {city}
            </span>
            <span className='tou__rating d-flex align-items-center gap-1'>
               <i className="ri-star-fill"></i> {avgRating}{" "}
               <span>({reviews.length})</span>
            </span> 
         </div>

         <h5 className="tour__title"><Link to = {`/tour/${id}`}>{title}</Link></h5>
      </CardBody>
    </div>
  )
}

export default TourCard
